floor_counter = 0
has_been_in_basement = False
basement_char_pos = 0


for i in range(len(input_str)):

    if input_str[i] == ")":
        floor_counter -= 1
    elif input_str[i] == "(":
        floor_counter += 1

    if floor_counter == -1 and not has_been_in_basement:
        basement_char_pos = i + 1 # offset character count otherwise off by 1
        has_been_in_basement = True

print(f"target floor: {floor_counter}")
print(f"entered basement at pos {basement_char_pos}")